ALTER TABLE `article`
  ADD PRIMARY KEY (`article_id`),
  ADD UNIQUE KEY `url` (`url`),
  ADD KEY `uzivatel_id` (`author_id`),
  ADD KEY `edited_author_id` (`edited_author_id`);

ALTER TABLE `article`
    MODIFY `article_id` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=1;

ALTER TABLE `article`
  ADD CONSTRAINT `article_ibfk_1` FOREIGN KEY (`author_id`) REFERENCES `user` (`user_id`),
  ADD CONSTRAINT `article_ibfk_2` FOREIGN KEY (`edited_author_id`) REFERENCES `user` (`user_id`);